workflow-item.script:
var cmd = null;
var hostlist = hosts.split(',');
successfulHosts = new Properties();
failedHosts = new Properties();

System.log("Iterating " + hostlist.length + " hosts...");
hostlist.forEach(function (host) {
    try {
        //remove any whitespace:
        host = host.replace(/\s/g, '');
        if (host.length > 0) {
            cmd = new SSHCommand(host, username, password);
            var result = execSSH(cmd, script, host);

            if (rebootHosts == true) {
                execSSH(cmd, "shutdown -r 1", host);
            }
            successfulHosts.put(host, result);
        }
    } catch (e) {
        failedHosts.put(host, e);
        if (continueOnError == false) {
            throw e;
        }
    }
    finally {
        try {
            if (cmd != null) {
                cmd.disconnect();
                System.log("Disconnected from SSH Host");
            }
        } catch (e) { }
    }
});

function execSSH(cmdObject, cmdText, host) {
    System.debug("Executing command on remote SSH Host: " + host + "...");

    var cmdResult = cmdObject.executeCommand(cmdText, true);
    //System.debug("Command state:" + cmdObject.state);
    if (cmdObject.state != "error") {
        System.log(host + ": " + cmdResult);
    }
    else
        throw "Received Error from SSH command: " + cmd.error;
    
    return cmdResult;
}
